rgin: 0;  padding: 0;  box-shadow: 2px 2px 10px var(--rm-overlay);  border: none;  border-radius: 50%;  background: none;  color: var(--rm-white);  opacity: 0.8;}.video-player .play-btn:hover {  box-shadow: 5px 5px 15px var(--rm-overlay);  opacity: 1;}.video-player img {  display: block;}.video-player iframe,.video-player object,.video-player embed,.video-player img {  height: 100%;  width: 100%;  position: absolute;  left: 0;  top: 0;}/* The Objectives Section  */.objectives-section {  color: var(--rm-primary);  background-color: var(--rm-light);  background-image: url(../img/bg/the-objectives.svg);  background-position: left top;  background-size: 240px auto;  background-repeat: no-repeat;}/* Our Vision Section  */.vision-section {  color: var(--rm-white);  background-color: var(--rm-primary);  background-image: url(../img/bg/our-vision.svg);  background-position: right center;  background-size: 420px auto;  background-repeat: repeat-y;}/* Educating a Child is a Trust */.educating-child-section {  color: var(--rm-white);  background-color: var(--rm-primary);  background-image: url(../img/bg/educating-child.svg);  background-position: left center;  background-size: 420px auto;  background-repeat: repeat-y;}/* Parent & Madrasah Partnership */.parent-partnership-section {  color: var(--rm-secondary);  background-color: var(--rm-white);  background-image: url(../img/bg/parent-partnership.svg);  background-position: right top;  background-size: 420px auto;  background-repeat: no-repeat;}/* Teachers Tab Section  */.teachers-tab-section {  color: var(--rm-green);  background-color: var(--rm-green-light);}.teachers-list-items {  display: flex;  align-items: flex-start;  justify-content: flex-start;  gap: 1.5rem;}.teachers-list-items .techers-photo {  width: 30%;}.teachers-list-items .techers-info {  width: 70%;}/* Principles of our Teachers */.principles-teachers-section {  color: var(--rm-white);  background-color: var(--rm-secondary);  background-image: url(../img/bg/pr