ding-levels-item .ascending-levels-details {  width: 80%;}.ascending-levels-item .ascending-levels-details p {  margin-bottom: 0.15rem;}/* Classes & Calendar Page  */.madrasah-classes-section {  color: var(--rm-primary);  background-color: var(--rm-white);}.madrasah-classes-image img {  width: 380px;}.levels-section {  color: var(--rm-white);  background-color: var(--rm-green);}.levels-section .levels-img {  width: 220px;}.madrasah-calendar-section {  color: var(--rm-secondary);  background-color: var(--rm-light);}.important-dates-section {  color: var(--rm-secondary);  background-color: var(--rm-primary);  background-image: url(../img/bg/important-dates.svg);  background-position: right center;  background-size: 420px auto;  background-repeat: repeat-y;}/* Parents Page */.parent-handbook-section {  color: var(--rm-primary);  background-color: var(--rm-white);  background-image: url(../img/bg/fees-bg.svg);  background-position: right center;  background-size: 420px auto;  background-repeat: repeat-y;}.parent-handbook-section .download-btn {  width: auto;  padding: 0.75rem 1rem;  margin-bottom: 1rem;  line-height: 1em;  text-decoration: none;  border: 1px solid var(--rm-primary);  background-color: var(--rm-primary);  color: var(--rm-white);  display: flex;  align-items: center;  justify-content: center;}.parent-handbook-section .download-btn:hover {  border: 1px solid var(--rm-secondary);  background-color: var(--rm-secondary);  color: var(--rm-white);}.uniform-section {  color: var(--rm-green);  background-color: var(--rm-light);}.uniform-section-image img {  width: 380px;}.fees-section {  color: var(--rm-primary);  background-color: var(--rm-gray);  background-image: url(../img/bg/fees-bg.svg);  background-position: right center;  background-size: 420px auto;  background-repeat: repeat-y;}.scholarships-section {  color: var(--rm-white);  background-color: var(--rm-primary);  background-image: url(../img/bg/scholarships.svg);  background-position: left top;  background-size: 240px auto;  background-repeat: no-repeat;}/* FAQ Page */.faqs-section {  color: var(--rm-secondary);  background-color: var(--rm-light);  background-image: url(../img/bg/patern-dark.svg);  background-repeat: repeat;  background-size: 240px;}.faqs-section .faqs-row {  background-color: var(--rm-white);  padding: 1.5rem 2.5rem;  padding-left: 5rem;  border-radius: 1rem;  margin: 1.5rem 0;}.nav-faqs {  min-height: initial;  padding: 0.75rem 0;  /* background-color: var(--rm-green); */  background-color: var(--rm-dark);}.faq-nav {  display: flex;  width: 100%;  gap: 0.25rem;  align-items: center;  text-align: center;  justify-content: stretch;  flex-wrap: wrap;  list-style: none;  font-family: var(--rm-font-serif);  margin-bottom: 0;  padding-left: 0;}.faq-nav .faq-nav-link {  background-color: transparent;  display: block;  height: 100%;  padding: 1rem;  width: calc((100% / 4) - 0.5rem);  cursor: pointer;  opacity: 0.9;}.faq-nav .faq-nav-link:hover,.faq-nav .faq-nav-link.active {  background-color: transparent;  opacity: 1;}.faq-nav .faq-nav-link a {  font-family: var(--rm-font-serif);  font-weight: normal;  text-decoration: none;  color: var(--rm-black);}.faq-nav .faq-nav-link.active a,.faq-nav .faq-nav-link:hover a {  /* text-decoration: underline; */  font-weight: bold;  color: var(--rm-black);}.faq-question {  display: list-item;  padding-left: 0.5rem;  margin-left: -0.5rem;}.faq-question::marker {  content: "\25C6";}/* Support Us Page  */.zakah-learning-section {  color: var(--rm-white);  background-color: var(--rm-green);}.zakah-learning-image img {  width: 380px;}.support-section {  color: var(--rm-primary);  background-color: var(--rm-gray);  background-image: url(../img/bg/support-us.svg);  background-position: right center;  background-size: 420px auto;  background-repeat: repeat-y;}/* Registration Page */.registration-section {  color: var(--rm-white);  background-color: var(--rm-primary);  background-image: url(../img/bg/patern-light.svg);  background-repeat: repeat;  background-size: 240px;}.registration-section .form-section {  background-color: var(--rm-primary-dark);  padding: 1.5rem;  border-radius: 1rem;  margin: 1.5rem 0;}/* Contact Us Page  */.contact-us-section {  color: var(--rm-white);  background-color: var(--rm-primary);  background-image: url(../img/bg/contact-us.svg);  background-position: right center;  background-size: 420px auto;  background-repeat: repeat-y;}.contact-us-section .contact-info {  padding-left: 2rem;  border-left: 1px solid rgba(255, 255, 255, 0.4);  height: 100%;}.contact-us-section .contact-info p {  font-weight: bold;}.contact-us-section ul.address-info {  padding-left: 0;  color: var(--rm-white);}.contact-us-section ul.address-info li a {  color: var(--rm-white);}.contact-us-section .contact-form .btn {  color: var(--rm-white);  background-color: var(--rm-green);  border-color: var(--rm-green);  padding: 0.75rem 1.5rem;  margin: 0;  display: block;  float: right;  text-align: center;  min-width: 240px;  font-weight: bold;}/* Complaint Form  */.complaint-form-section {  color: var(--rm-white);  background-color: var(--rm-primary);  background-image: url(../img/bg/complaint-form-bg.svg);  background-position: right center;  background-size: 420px auto;  background-repeat: repeat-y;}.complaint-form-section .complaint-form-body .btn {  color: var(--rm-white);  background-color: var(--rm-green);  border-color: var(--rm-green);  padding: 0.75rem 1.5rem;  margin: 1.5rem auto;  display: block;  text-align: center;  min-width: 240px;  font-weight: bold;}.complaint-form-section .complaint-form-body .btn:hover {  background-color: var(--rm-secondary);  border-color: var(--rm-secondary);}/* Footer Section  */.footer-main {  padding: 3rem 0;  padding-bottom: 1rem;  margin: 0 auto;  min-height: auto;  background: var(--rm-white);  color: var(--rm-primary);}.footer-logo {  margin: 0;  margin-bottom: 1.5rem;  margin-right: 1.5rem;}.footer-logo img {  max-width: 100px;}.footer-title {  