    <script>
    $(document).ready(function() {
        $('#complaintFormMustafaMount_form').validate({
            rules: {
                yourname: {
                    required: true
                },
                pubilname: {
                    required: true
                },
                relationship: {
                    required: true
                },
                address: {
                    required: true
                },
                postalcode: {
                    required: true
                },
                daytimephonenumberMM: {
                    required: true,
                    minlength: 11,
                    maxlength: 11,
                },
                eveningphonenumberMM: {
                    required: true,
                    minlength: 11,
                    maxlength: 11,
                },
                details: {
                    required: true
                },
                detailsaction: {
                    required: true
                },
                'g-recaptcha-response': {
                    required: function() {
                        return grecaptcha.getResponse() === '';
                    }
                }
            },
            messages: {
                yourname: {
                    required: "Please enter your name."
                },
                pubilname: {
                    required: "Please enter pupil's name."
                },
                relationship: {
                    required: "Please select your relationship to the pupil."
                },
                address: {
                    required: "Please enter your address."
                },
                postalcode: {
                    required: "Please enter your postcode."
                },
                daytimephonenumberMM: {
                    required: "Please enter your daytime phone number."
                },
                eveningphonenumberMM: {
                    required: "Please enter your evening phone number."
                },
                details: {
                    required: "Please enter details of your complaint."
                },
                detailsaction: {
                    required: "Please enter details of action taken."
                },
                'g-recaptcha-response': {
                    required: "Please complete the reCAPTCHA."
                }
            },
            errorElement: 'span',
            errorPlacement: function(error, element) {
                error.addClass('invalid-feedback');
                element.closest('.form-group').append(error);
            },
            highlight: function(element, errorClass, validClass) {
                $(element).addClass('is-invalid').removeClass('is-valid');
            },
            unhighlight: function(element, errorClass, validClass) {
                $(element).removeClass('is-invalid').addClass('is-valid');
            },
            submitHandler: function(form) {

jQuery('#preloader').show();
jQuery('#loading').show();
var formData = $(form).serialize();

$.ajax({
    type: 'POST',
    url: '<?= base_url('Home/complaintsubmit') ?>',
    data: formData,
    dataType: 'json',
    success: function(response) {
        if (response.success) {
            window.scrollTo({
                top: 0,
                left: 0,
                behavior: 'smooth'
            });
            form.reset();
            grecaptcha.reset();
            jQuery('#preloader').hide();
            jQuery('#loading').hide();
           //  window.location.href = "<?=base_url('thankyou')?>";
             toastr.success('Thanks for reaching out Rawdah Madrasah. Your information submitted successfully. Our team will contact you soon', 'Success');
             window.location.reload();

        } else {
            grecaptcha.reset();
            jQuery('#preloader').hide();
            jQuery('#loading').hide();

            toastr.error(response.message, 'Error');
            console.log(response.message);
        }
    },
    error: function(xhr, status, error) {
        grecaptcha.reset();
        jQuery('#preloader').hide();
        jQuery('#loading').hide();
        toastr.error('Error submitting form', 'Error');
    }
});

return false;
}
        });
    });
</script>
